import 'package:flutter/material.dart';import 'package:hiberusapp/constants.dart';import 'package:hiberusapp/screens/home_screen.dart';void main() => runApp(MyApp());class MyApp extends StatelessWidget {    @override    Widget build(BuildContext context) {        final appTitle = 'Hiberus App';        return MaterialApp(            debugShowCheckedModeBanner: false,            title: appTitle,            theme: ThemeData(                textTheme: Theme.of(context).textTheme.apply(bodyColor: kTextColor),            ),            home: HomeScreen(),        );    }}